            "pos_type": 0,
            "tags": "[\"\\u533a\\u5757\\u94fe\",\"\\u5e94\\u7528\\u843d\\u5730\",\"\\u5b89\\u5168\",\"\\u533f\\u540d\",\"\\u53bb\\u4e2d\\u5fc3\\u5316\"]",
